import {TextInput,Button} from "react-native-paper";
import React, { useState } from "react";
import {View,StyleSheet} from "react-native";
import firebase from "../database/firebaseConfig";


const EditScreen = (props) => {
    // let [tasks, setTasks] = useState([]);
    let [description, setDescription] = useState(props.navigation.state.params.description);

    const updateTask = (id) => {
      firebase.db.collection('tasks').doc(id).update({
        description,
      });
      props.navigation.navigate('Task', {
        taskUpdatedId: props.navigation.state.params.id,
      });
    }

    return (
        <View style={styles.container}>
            <TextInput
            mode = "outlined"
            style={styles.description}
            placeholder="Nova descrição da tarefa"
            // defaultValue={}
            value={description}
            onChangeText={(newValue) => setDescription(newValue)}
            />

            <Button style={styles.taskBtn} mode="contained" onPress={() => updateTask(props.navigation.state.params.id)}>
              Editar tarefa
            </Button>


        </View>
    );
}

const styles = StyleSheet.create({
    container:{
      width:'96%',
      marginRight:'2%',
      marginLeft:'2%',
  },
    description:{
      marginTop:20,
    },
    tasksList: {
      marginTop: 20,
    },
    taskBtn: {
      marginTop: 20,
    },
    icon:{
      marginTop:5
    }
  });

export default EditScreen;
