import React from "react";
import { Text, StyleSheet, View, Image,TouchableOpacity} from "react-native";
import { FontAwesome } from '@expo/vector-icons';
import { List } from "react-native-paper";
import { AntDesign } from '@expo/vector-icons'; 


const TaskItem = (props) => {
  return (
      <List.Item
        title={props.task.date}
        description={props.task.description}
        left={(paperProps) => <FontAwesome {...paperProps} name="tasks" style={styles.icon} size={24} color="black"/>}
        right={(paperProps) => 

        <View style={styles.close}>
          <TouchableOpacity style={styles.icon} onPress={() => props.removeTask(props.task.id)}>
              <AntDesign {...paperProps} style={styles.IconSpace} name="closesquare" size={24} color="#ff4757" />
          </TouchableOpacity>  

          <TouchableOpacity style={styles.icon} onPress={() => props.navigation.navigate('Edit',{
            description: props.task.description,
            id: props.task.id
          })}>
              <AntDesign {...paperProps} name="edit" size={24} color="orange" />
          </TouchableOpacity>  
        </View>
      }
      />
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap",
    alignItems: "flex-start",
    marginTop: 20,

  },
  imageColumn: {
    width: '8%',
    marginLeft:'2%'
  },
  descColumn: {
    width: '60%',
  },
  dateColumn: {
    width: '23%',
  },
  removeColumn: {
    width: '5%',
    alignItems: 'center',
    marginRight:'2%'
  },
  xColor:{
    color:'white'
  },
  icon:{
    marginTop:12,
    paddingLeft:25
  },
  close:{
    flex:1,
    flexDirection:"row",
    paddingLeft:25
  },
  IconSpace: {
    paddingRight:0
  }
});

export default TaskItem;
